import React from 'react';
import { useLocation } from 'react-router-dom';
import Menulink from "./widget/menu_link_widget.js";
import MenuSubLink from './widget/menu_sub_link_widget.js';

const Sidebar = () => {


  const location = useLocation(); // 현재 URL 정보를 가져옴

  // 각 메뉴의 URL 매칭을 확인하는 함수
  const isActive = (path) => location.pathname === path;

  return (
    <div className="row w300px bg-main padding-to-5em">
      <button type="button" className="bg-sub btn"><span className="text_href">새 프로젝트 추가</span></button>
      <nav className="row padding-y-2em">
        <ul>
          <Menulink MenuIcon="Dashboard" MenuActvie={isActive('/') ? true : false} MenuName="대시보기" MenuHref="/" />
          <MenuSubLink MenuIcon="Project" MenuName="내 프로젝트"  />
          <MenuSubLink MenuIcon="user" MenuName="팀 활동"  />
          <Menulink MenuIcon="Comments" MenuName="코멘트 및 피드백" MenuHref="#" />
          <Menulink MenuIcon="Commuting" MenuActvie={isActive('/commute-view') ? true : false} MenuName="출퇴근 및 기타관리" MenuHref="/commute-view" />
          <Menulink MenuIcon="ActivityLog" MenuActvie={isActive('/log') ? true : false} MenuName="활동로그" MenuHref="" />
        </ul>
      </nav>
      <button className="bg-sub btn w70 margin-auto">
        <svg xmlns="http://www.w3.org/2000/svg" height="24px" viewBox="0 -960 960 960" width="24px"
          fill="#FFFFFF">
          <path
            d="m612-292 56-56-148-148v-184h-80v216l172 172ZM480-80q-83 0-156-31.5T197-197q-54-54-85.5-127T80-480q0-83 31.5-156T197-763q54-54 127-85.5T480-880q83 0 156 31.5T763-763q54 54 85.5 127T880-480q0 83-31.5 156T763-197q-54 54-127 85.5T480-80Zm0-400Zm0 320q133 0 226.5-93.5T800-480q0-133-93.5-226.5T480-800q-133 0-226.5 93.5T160-480q0 133 93.5 226.5T480-160Z" />
        </svg>
        <span className="text_href margin-left-1em">출근하기</span>
      </button>
    </div>
  );
};

export default Sidebar;
